cust_event=#{cust_event}, cust_fax=#{cust_fax}, cust_homepage=#{cust_homepage}, emp_id=#{emp_id}, emp_email=#{emp_email}, cust_etc=#{cust_etc}, cust_status=#{cust_status}
where cust_id=#{cust_id}
