mapper.insert:

UPDATE orders o
LEFT JOIN in_material i
ON o.order_id = i.order_id
SET i.in_id = #{in_id}, i.in_process = "입고완료", i.in_emp = #{in_emp}
WHERE o.order_id = #{order_id}

mapper.select:
SELECT o.order_id, o.due_date, o.order_date, o.order_emp, i.in_id, i.in_process, o.in_date, i.in_emp, m.ma_name, o.order_qty, m.ma_qty, m.shelt_position
FROM in_material i, material m, orders o
WHERE m.ma_id = o.ma_id AND o.order_id = i.order_id AND o.order_id = #{order_id}
